mapper.select:
select item_id,item_name,item_discribe,item_committeeid,item_score,identity_name
from identity id,item it
where it.item_committeeid=id.identity_id limit #{firstIndex} , #{limit}

mapper.update:
update item

	item_name=#{itemName},
	item_discribe=#{itemDiscribe},
	item_committeeid=#{itemCommitteeid},
	item_score=#{itemScore}


where item_id=#{itemId}
